SELECT INSF.INS_ID, INSF.INS_CODE, INSF.INS_NAME, MCHT.MCHNT_ID, MCHT.MCHNT_CODE, MCHT.MCHNT_NAME, PROD.PRODUCT_CODE, PROD.PRODUCT_NAME FROM TB_PRODUCT PROD, TB_REL_INS_PRODUCT REPI, TB_INS_INF INSF, TB_MERCHANT_INF MCHT WHERE MCHT.INS_ID = INSF.INS_ID AND INSF.INS_ID = REPI.INS_ID AND REPI.PRODUCT_CODE = PROD.PRODUCT_CODE AND PROD.DATA_STAT = '0' AND REPI.DATA_STAT = '0' AND INSF.DATA_STAT = '0' AND MCHT.DATA_STAT = '0'
AND PRODUCT_CODE=#{productCode}
AND INS_ID=#{insId}
AND INS_CODE=#{insCode}
AND MCHNT_ID=#{mchntId}
AND MCHNT_CODE=#{mchntCode}
