mapper.select:
select  a.MCHNT_CODE from TB_MERCHANT_INF a, TB_SHOP_INF b 
where a.MCHNT_ID = b.MCHNT_ID 
and b.SHOP_CODE = #{shopCode}

mapper.update:
update TB_WEB_DATA_AUTH
MCHNT_CODE = #{mchntCode,jdbcType=VARCHAR}, SHOP_CODE = #{shopCode,jdbcType=VARCHAR}, CASECADE = #{casecade,jdbcType=DECIMAL}, REMARKS = #{remarks,jdbcType=VARCHAR}, UPDATE_USER = #{updateUser,jdbcType=VARCHAR}, UPDATE_TIME = sysdate, LOCK_VERSION = LOCK_VERSION +1
where ID = #{id,jdbcType=VARCHAR}
