mapper.select:
SELECT
IL.DMS_RELATED_KEY AS REFERENCE_NO, IL.CREATE_TIME AS TRANS_TIME, M.MCHNT_NAME, S.SHOP_NAME, P.PERSONAL_NAME AS PERSON_NAME, NVL(IO.INVOICE_STAT,0) AS INVOICE_STAT FROM TB_TRANS_LOG${tableNum} TL INNER JOIN TB_INTFACE_TRANS_LOG${tableNum} IL ON IL.INTERFACE_PRIMARY_KEY = TL.DMS_RELATED_KEY AND TL.TRANS_ID = IL.TRANS_ID LEFT JOIN TB_MERCHANT_INF M ON TL.MCHNT_CODE = M.MCHNT_CODE LEFT JOIN TB_SHOP_INF S ON TL.SHOP_CODE = S.SHOP_CODE INNER JOIN TB_SHOP_INF S1 ON S.P_SHOP_CODE = S1.SHOP_CODE LEFT JOIN TB_ACCOUNT_INF AI ON AI.ACCOUNT_NO = TL.PRI_ACCT_NO LEFT JOIN TB_USER_INF U ON U.USER_ID = AI.USER_ID LEFT JOIN TB_PERSON_INF P ON P.PERSONAL_ID = AI.PERSONAL_ID LEFT JOIN TB_INVOICE_ORDER IO ON IO.ITF_PRIMARY_KEY = TL.DMS_RELATED_KEY WHERE 1 = 1 AND TL.TRANS_ID IN ('W10','W11','W71','W74','W30','W75') AND TL.TRANS_ST IN ('1','3','17') AND TL.RESP_CODE = '00' AND TL.MCHNT_CODE = #{mchntCode} AND S1.SHOP_CODE = #{shopCode}
AND S.SHOP_NAME LIKE '%' || #{shopName} || '%'
AND P.PERSONAL_NAME LIKE '%' || #{personName} || '%'
AND TL.TRANS_ID=#{transId}
AND TL.TRANS_ST=#{transSt}
AND SUBSTR(TL.DMS_RELATED_KEY,-6)=#{dmsRelatedKey}
AND IO.INVOICE_STAT = #{invoiceStat}
ORDER BY TRANS_TIME DESC

SELECT
IL.DMS_RELATED_KEY AS REFERENCE_NO, IL.CREATE_TIME AS TRANS_TIME, M.MCHNT_NAME, S.SHOP_NAME, P.PERSONAL_NAME AS PERSON_NAME, NVL(IO.INVOICE_STAT,0) AS INVOICE_STAT FROM TB_TRANS_LOG_HIS TL INNER JOIN TB_INTFACE_TRANS_LOG_HIS IL ON IL.INTERFACE_PRIMARY_KEY = TL.DMS_RELATED_KEY AND TL.TRANS_ID = IL.TRANS_ID LEFT JOIN TB_MERCHANT_INF M ON TL.MCHNT_CODE = M.MCHNT_CODE LEFT JOIN TB_SHOP_INF S ON TL.SHOP_CODE = S.SHOP_CODE INNER JOIN TB_SHOP_INF S1 ON S.P_SHOP_CODE = S1.SHOP_CODE LEFT JOIN TB_ACCOUNT_INF AI ON AI.ACCOUNT_NO = TL.PRI_ACCT_NO LEFT JOIN TB_USER_INF U ON U.USER_ID = AI.USER_ID LEFT JOIN TB_PERSON_INF P ON P.PERSONAL_ID = AI.PERSONAL_ID LEFT JOIN TB_INVOICE_ORDER IO ON IO.ITF_PRIMARY_KEY = TL.DMS_RELATED_KEY WHERE 1 = 1 AND TL.TRANS_ID IN ('W10','W11','W71','W74','W30','W75') AND TL.TRANS_ST IN ('1','3','17') AND TL.RESP_CODE = '00' AND TL.MCHNT_CODE = #{mchntCode} AND S1.SHOP_CODE = #{shopCode}
AND S.SHOP_NAME LIKE '%' || #{shopName} || '%'
AND P.PERSONAL_NAME LIKE '%' || #{personName} || '%'
AND TL.TRANS_ID=#{transId}
AND TL.TRANS_ST=#{transSt}
AND SUBSTR(TL.DMS_RELATED_KEY,-6)=#{dmsRelatedKey}
AND IO.INVOICE_STAT = #{invoiceStat}
AND IL.CREATE_TIME >= to_date(#{startTransTime}, 'YYYY-MM-DD HH24:MI:SS')
AND IL.CREATE_TIME <= to_date(#{endTransTime}, 'YYYY-MM-DD HH24:MI:SS')
ORDER BY TRANS_TIME DESC
