trans_st = #{transSt,jdbcType=INTEGER}, resp_code = #{respCode,jdbcType=VARCHAR}, update_time = SYSDATE, org_interface_primary_key=#{orgInterfacePrimaryKey,jdbcType=VARCHAR}, dms_related_key=#{dmsRelatedKey,jdbcType=VARCHAR}, org_dms_related_key=#{orgDmsRelatedKey,jdbcType=VARCHAR}, additional_info=#{additionalInfo,jdbcType=VARCHAR},
WHERE interface_primary_key = #{interfacePrimaryKey,jdbcType=VARCHAR}

UPDATE TB_INTFACE_TRANS_LOG_HIS
TRANS_ST = #{transSt,jdbcType=INTEGER}, RESP_CODE = #{respCode,jdbcType=VARCHAR}, UPDATE_TIME = SYSDATE, org_interface_primary_key=#{orgInterfacePrimaryKey,jdbcType=VARCHAR}, dms_related_key=#{dmsRelatedKey,jdbcType=VARCHAR}, org_dms_related_key=#{orgDmsRelatedKey,jdbcType=VARCHAR}, additional_info=#{additionalInfo,jdbcType=VARCHAR},
WHERE INTERFACE_PRIMARY_KEY = #{interfacePrimaryKey,jdbcType=VARCHAR}

