mapper.select:
SELECT 

   FROM TB_BATCH_ORDER_LIST L
  WHERE L.DATA_STAT='0' 
  AND L.ORDER_ID = #{orderId} 
  AND L.PHONE_NO=#{phoneNo}

mapper.insert:
INSERT INTO TB_BATCH_ORDER_LIST( ORDER_LIST_ID, ORDER_ID, USER_NAME, PHONE_NO, USER_CARD_NO, PRODUCT_CODE, PRODUCT_NAME, AMOUNT, ORDER_STAT, ORDER_DESC, DATA_STAT, REMARKS, CREATE_USER, UPDATE_USER, CREATE_TIME, UPDATE_TIME, LOCK_VERSION) SELECT TO_CHAR(sysdate, 'YYYYMMDDHHMISS') || to_char(seq_oms_all_tb_id.nextval, 'fm00000000') as ORDER_LIST_ID, ORDER_ID, USER_NAME, PHONE_NO, USER_CARD_NO, PRODUCT_CODE, PRODUCT_NAME, AMOUNT, ORDER_STAT, ORDER_DESC, DATA_STAT, REMARKS, CREATE_USER, UPDATE_USER, CREATE_TIME, UPDATE_TIME, LOCK_VERSION FROM(
select #{item.orderId,jdbcType=VARCHAR} as ORDER_ID, #{item.userName,jdbcType=VARCHAR} as USER_NAME, #{item.phoneNo,jdbcType=VARCHAR} as PHONE_NO, #{item.userCardNo,jdbcType=VARCHAR} as USER_CARD_NO, #{item.productCode,jdbcType=VARCHAR} as PRODUCT_CODE, #{item.productName,jdbcType=VARCHAR} as PRODUCT_NAME, #{item.amount,jdbcType=VARCHAR} as AMOUNT, #{item.orderStat,jdbcType=VARCHAR} as ORDER_STAT, #{item.orderDesc,jdbcType=VARCHAR} as ORDER_DESC, '0' as DATA_STAT, #{item.remarks,jdbcType=VARCHAR} as REMARKS, #{item.createUser,jdbcType=VARCHAR} as CREATE_USER, #{item.updateUser,jdbcType=VARCHAR} as UPDATE_USER, sysdate as CREATE_TIME, sysdate as UPDATE_TIME, '0' as LOCK_VERSION from dual
) A

mapper.update:
UPDATE TB_BATCH_ORDER_LIST
ORDER_ID = #{orderId,jdbcType=VARCHAR}, USER_NAME = #{userName,jdbcType=VARCHAR}, PHONE_NO = #{phoneNo,jdbcType=VARCHAR}, USER_CARD_NO = #{userCardNo,jdbcType=VARCHAR}, PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}, PRODUCT_NAME = #{productName,jdbcType=VARCHAR}, AMOUNT = #{amount,jdbcType=VARCHAR}, ORDER_STAT = #{orderStat,jdbcType=VARCHAR}, ORDER_DESC = #{orderDesc,jdbcType=VARCHAR}, DATA_STAT = #{dataStat,jdbcType=VARCHAR}, REMARKS = #{remarks,jdbcType=VARCHAR}, UPDATE_USER = #{updateUser,jdbcType=VARCHAR}, UPDATE_TIME = SYSDATE, LOCK_VERSION = LOCK_VERSION + 1
WHERE ORDER_LIST_ID = #{orderListId,jdbcType=VARCHAR}
