mapper.select:
SELECT O.ORDER_ID, O.PRODUCT_CODE, O.PRODUCT_NAME, O.ORDER_NAME, O.ORDER_TYPE, O.ORDER_DATE, O.ORDER_STAT, O.DATA_STAT, O.REMARKS, O.CREATE_USER, O.UPDATE_USER, O.CREATE_TIME, O.UPDATE_TIME, O.LOCK_VERSION, O.COMPANY_NAME, O.BIZ_TYPE, (SELECT COUNT(L.ORDER_LIST_ID) FROM TB_BATCH_ORDER_LIST L WHERE L.DATA_STAT='0' AND O.ORDER_ID = L.ORDER_ID) AS ORDERCOUNT, (SELECT SUM(L.AMOUNT) FROM TB_BATCH_ORDER_LIST L WHERE L.DATA_STAT = '0' AND L.ORDER_ID = O.ORDER_ID) AS SUMAMOUNT, NVL(BI.INVOICE_STAT,0) AS INVOICE_STAT FROM TB_BATCH_ORDER O LEFT JOIN TB_BATCH_INVOICE_ORDER BI ON O.ORDER_ID = BI.ORDER_ID WHERE O.DATA_STAT = '0' and O.ORDER_TYPE=#{orderType}
AND O.ORDER_ID = #{orderId}
AND O.ORDER_NAME LIKE '%'|| #{orderName} ||'%'
AND O.PRODUCT_CODE = #{productCode}
AND O.ORDER_STAT = #{orderStat}
AND O.BIZ_TYPE = #{bizType}
AND BI.INVOICE_STAT is null AND O.ORDER_STAT IN (40,90)
AND BI.INVOICE_STAT = #{invoiceStat}
AND O.ORDER_STAT != '40' AND O.ORDER_STAT != '90'
AND O.CREATE_TIME >= to_date(#{startTime}, 'YYYY-MM-DD HH24:MI:SS')
AND O.CREATE_TIME <= to_date(#{endTime}, 'YYYY-MM-DD HH24:MI:SS')
ORDER BY O.UPDATE_TIME DESC

mapper.update:
UPDATE TB_BATCH_ORDER
PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}, PRODUCT_NAME = #{productName,jdbcType=VARCHAR}, ORDER_NAME = #{orderName,jdbcType=VARCHAR}, ORDER_TYPE = #{orderType,jdbcType=VARCHAR}, ORDER_STAT = #{orderStat,jdbcType=VARCHAR}, DATA_STAT = #{dataStat,jdbcType=VARCHAR}, REMARKS = #{remarks,jdbcType=VARCHAR}, UPDATE_USER = #{updateUser,jdbcType=VARCHAR}, COMPANY_NAME = #{companyName,jdbcType=VARCHAR}, BIZ_TYPE = #{bizType,jdbcType=VARCHAR}, UPDATE_TIME = sysdate, LOCK_VERSION = LOCK_VERSION+1
WHERE ORDER_ID = #{orderId,jdbcType=VARCHAR}
