mapper.select:
SELECT , il.DMS_RELATED_KEY AS REFERENCE_NO, il.CREATE_TIME AS TRANS_TIME, m.MCHNT_NAME, s.SHOP_NAME, p.PERSONAL_NAME AS PERSON_NAME FROM TB_TRANS_LOG${tableNum} tl INNER JOIN TB_INTFACE_TRANS_LOG${tableNum} il ON il.INTERFACE_PRIMARY_KEY=tl.DMS_RELATED_KEY AND tl.TRANS_ID=il.TRANS_ID LEFT JOIN TB_MERCHANT_INF m ON tl.MCHNT_CODE=m.MCHNT_CODE LEFT JOIN TB_SHOP_INF s ON tl.SHOP_CODE=s.SHOP_CODE LEFT JOIN Tb_Account_Inf ai on ai.ACCOUNT_NO = tl.pri_acct_no LEFT JOIN TB_USER_INF u ON u.USER_ID=ai.USER_ID LEFT JOIN TB_PERSON_INF p ON p.PERSONAL_ID = ai.PERSONAL_ID WHERE 1=1
AND tl.MCHNT_CODE = #{mchntCode}
AND p.PERSONAL_NAME LIKE '%' || #{userName} || '%'
and tl.TRANS_CHNL in
${item}
AND tl.TRANS_ID=#{transType}
AND il.CREATE_TIME >= to_date(#{startTime}, 'YYYY-MM-DD HH24:MI:SS')
AND il.CREATE_TIME <= to_date(#{endTime}, 'YYYY-MM-DD HH24:MI:SS')
ORDER BY TRANS_TIME DESC

