PCI.CHANNEL_NAME as payChannelName FROM TB_WX_TRANS_LOG${tableNum} TL INNER JOIN TB_INTFACE_TRANS_LOG${tableNum} IL ON IL.DMS_RELATED_KEY = TL.WX_PRIMARY_KEY LEFT JOIN TB_MERCHANT_INF M ON IL.MCHNT_CODE=M.MCHNT_CODE LEFT JOIN TB_SHOP_INF S ON IL.SHOP_CODE=S.SHOP_CODE LEFT JOIN TB_WX_CLIENT_FANS CF ON CF.OPENID = IL.USER_INF_USER_NAME LEFT JOIN TB_PAYMENT_CHANNELS_INF PCI ON PCI.CHANNEL_NO = IL.REMARKS WHERE 1=1 AND IL.REMARKS IS NOT NULL
