mapper.update:
UPDATE TB_PAYMENT_CHANNELS_API_INF
NAME =#{name,jdbcType=VARCHAR}, URL =#{url,jdbcType=VARCHAR}, API_TYPE =#{apiType,jdbcType=VARCHAR}, DESCRIPTION =#{description,jdbcType=VARCHAR}, ENABLE =#{enable,jdbcType=VARCHAR}, REMARKS =#{remarks,jdbcType=VARCHAR}, UPDATE_USER =#{updateUser,jdbcType=VARCHAR}, UPDATE_TIME =sysdate, LOCK_VERSION =LOCK_VERSION+1
WHERE ID=#{id}

mapper.select:
SELECT ,PCI.CHANNEL_NAME FROM TB_PAYMENT_CHANNELS_API_INF P LEFT JOIN TB_PAYMENT_CHANNELS_INF PCI ON P.CHANNEL_ID = PCI.ID WHERE 1=1 AND P.CHANNEL_ID = #{channelId}
AND P.NAME LIKE '%' || #{name} || '%'
AND P.api_type = #{apiType}
AND P.DATA_STAT = '0' ORDER BY P.UPDATE_TIME DESC
